import{_ as t,c as n,o,af as i}from"./chunks/framework.C3eVOF8n.js";const g=JSON.parse('{"title":"What is Continuous Integration, Continous Delivery, Continous Deployment?","description":"","frontmatter":{},"headers":[],"relativePath":"senior_growth/CD_CI/What_is_Continuous_Integration,_Continous_Delivery,_Continous_Deployment.md","filePath":"senior_growth/CD_CI/What_is_Continuous_Integration,_Continous_Delivery,_Continous_Deployment.md"}'),a={name:"senior_growth/CD_CI/What_is_Continuous_Integration,_Continous_Delivery,_Continous_Deployment.md"};function s(r,e,l,d,u,c){return o(),n("div",null,[...e[0]||(e[0]=[i('<h1 id="what-is-continuous-integration-continous-delivery-continous-deployment" tabindex="-1">What is Continuous Integration, Continous Delivery, Continous Deployment? <a class="header-anchor" href="#what-is-continuous-integration-continous-delivery-continous-deployment" aria-label="Permalink to ‚ÄúWhat is Continuous Integration, Continous Delivery, Continous Deployment?‚Äù">‚Äã</a></h1><h2 id="üîπ-ci-cd-concepts-continuous-integration-delivery-and-deployment" tabindex="-1">üîπ CI/CD Concepts: Continuous Integration, Delivery, and Deployment <a class="header-anchor" href="#üîπ-ci-cd-concepts-continuous-integration-delivery-and-deployment" aria-label="Permalink to ‚Äúüîπ CI/CD Concepts: Continuous Integration, Delivery, and Deployment‚Äù">‚Äã</a></h2><p>CI/CD is a set of <strong>practices to automate the build, testing, and delivery of software</strong>, improving quality and reducing release risk. Each part focuses on a different stage of the process.</p><hr><h2 id="_1Ô∏è‚É£-continuous-integration-ci" tabindex="-1">1Ô∏è‚É£ Continuous Integration (CI) <a class="header-anchor" href="#_1Ô∏è‚É£-continuous-integration-ci" aria-label="Permalink to ‚Äú1Ô∏è‚É£ Continuous Integration (CI)‚Äù">‚Äã</a></h2><p><strong>Definition:</strong><br> Continuous Integration is the practice of <strong>frequently integrating code changes into a shared repository</strong>. Every integration triggers <strong>automated builds and tests</strong>.</p><h3 id="key-points" tabindex="-1">Key Points: <a class="header-anchor" href="#key-points" aria-label="Permalink to ‚ÄúKey Points:‚Äù">‚Äã</a></h3><ul><li>Integrate multiple times a day to <strong>catch conflicts early</strong>.</li><li>Run <strong>unit tests, linting, type checks</strong> automatically.</li><li>Detect bugs <strong>early in the development cycle</strong>.</li></ul><h3 id="benefits" tabindex="-1">Benefits: <a class="header-anchor" href="#benefits" aria-label="Permalink to ‚ÄúBenefits:‚Äù">‚Äã</a></h3><ul><li>Faster feedback to developers.</li><li>Reduced integration issues.</li><li>Maintains a <strong>stable main branch</strong>.</li></ul><p><strong>Example Flow:</strong></p><ol><li>Developer pushes code to feature branch.</li><li>CI pipeline runs: <ul><li>Linting</li><li>Unit tests</li><li>Type checking</li><li>Build</li></ul></li><li>Feedback is provided automatically.</li></ol><hr><h2 id="_2Ô∏è‚É£-continuous-delivery-cd" tabindex="-1">2Ô∏è‚É£ Continuous Delivery (CD) <a class="header-anchor" href="#_2Ô∏è‚É£-continuous-delivery-cd" aria-label="Permalink to ‚Äú2Ô∏è‚É£ Continuous Delivery (CD)‚Äù">‚Äã</a></h2><p><strong>Definition:</strong><br> Continuous Delivery ensures that <strong>every change that passes CI is deployable to production</strong>, but deployment itself <strong>requires manual approval</strong>.</p><h3 id="key-points-1" tabindex="-1">Key Points: <a class="header-anchor" href="#key-points-1" aria-label="Permalink to ‚ÄúKey Points:‚Äù">‚Äã</a></h3><ul><li>Build, test, and prepare code for <strong>staging or production</strong>.</li><li>Deployment to production is <strong>controlled</strong>, not automatic.</li><li>Focus is on <strong>reliability and readiness</strong>.</li></ul><h3 id="benefits-1" tabindex="-1">Benefits: <a class="header-anchor" href="#benefits-1" aria-label="Permalink to ‚ÄúBenefits:‚Äù">‚Äã</a></h3><ul><li>Reduces manual steps in release.</li><li>Ensures <strong>production-ready builds</strong> at all times.</li><li>Minimizes risk of deployment failures.</li></ul><p><strong>Example Flow:</strong></p><ol><li>CI pipeline passes.</li><li>Build artifacts are deployed to <strong>staging environment</strong>.</li><li>QA approves, then code is <strong>manually deployed to production</strong>.</li></ol><hr><h2 id="_3Ô∏è‚É£-continuous-deployment-cd" tabindex="-1">3Ô∏è‚É£ Continuous Deployment (CD) <a class="header-anchor" href="#_3Ô∏è‚É£-continuous-deployment-cd" aria-label="Permalink to ‚Äú3Ô∏è‚É£ Continuous Deployment (CD)‚Äù">‚Äã</a></h2><p><strong>Definition:</strong><br> Continuous Deployment takes Continuous Delivery <strong>one step further</strong>: <strong>every change that passes tests is automatically deployed to production</strong>.</p><h3 id="key-points-2" tabindex="-1">Key Points: <a class="header-anchor" href="#key-points-2" aria-label="Permalink to ‚ÄúKey Points:‚Äù">‚Äã</a></h3><ul><li>No manual approval is needed.</li><li>Strong automated testing and monitoring is required.</li><li>Focus is on <strong>rapid and safe delivery</strong>.</li></ul><h3 id="benefits-2" tabindex="-1">Benefits: <a class="header-anchor" href="#benefits-2" aria-label="Permalink to ‚ÄúBenefits:‚Äù">‚Äã</a></h3><ul><li>Immediate delivery of new features and fixes.</li><li>Faster feedback from production.</li><li>Encourages <strong>small, incremental changes</strong>.</li></ul><p><strong>Example Flow:</strong></p><ol><li>CI pipeline passes.</li><li>Artifact is automatically deployed to <strong>production</strong>.</li><li>Monitoring tools check for issues; rollback if needed.</li></ol><hr><h2 id="_4Ô∏è‚É£-key-differences" tabindex="-1">4Ô∏è‚É£ Key Differences <a class="header-anchor" href="#_4Ô∏è‚É£-key-differences" aria-label="Permalink to ‚Äú4Ô∏è‚É£ Key Differences‚Äù">‚Äã</a></h2><table tabindex="0"><thead><tr><th>Aspect</th><th>CI</th><th>Continuous Delivery</th><th>Continuous Deployment</th></tr></thead><tbody><tr><td>Goal</td><td>Integrate code frequently</td><td>Ensure deployable builds</td><td>Deploy automatically</td></tr><tr><td>Deployment</td><td>‚ùå No deployment</td><td>‚úÖ Manual deployment</td><td>‚úÖ Automatic deployment</td></tr><tr><td>Feedback Speed</td><td>Fast</td><td>Medium (manual deploy)</td><td>Fast (production feedback)</td></tr><tr><td>Risk</td><td>Low</td><td>Low</td><td>Medium-high (needs monitoring)</td></tr></tbody></table><hr><h3 id="‚úÖ-summary" tabindex="-1">‚úÖ Summary <a class="header-anchor" href="#‚úÖ-summary" aria-label="Permalink to ‚Äú‚úÖ Summary‚Äù">‚Äã</a></h3><ul><li><strong>CI:</strong> Frequent integration + automated tests.</li><li><strong>Continuous Delivery:</strong> CI + deployable builds; manual deployment to production.</li><li><strong>Continuous Deployment:</strong> CI + automated deployment; code goes to production automatically.</li></ul><blockquote><p>üí° Senior Tip:<br> The <strong>main difference</strong> between Delivery and Deployment is <strong>manual vs automatic release to production</strong>. Both require <strong>strong CI and test automation</strong> to be safe.</p></blockquote>',37)])])}const h=t(a,[["render",s]]);export{g as __pageData,h as default};
