import{_ as t,c as o,o as n,af as a}from"./chunks/framework.C3eVOF8n.js";const m=JSON.parse('{"title":"What is a style guide?","description":"","frontmatter":{},"headers":[],"relativePath":"senior_growth/Clean_code_and_Code_Quality/What_is_a_style_guide.md","filePath":"senior_growth/Clean_code_and_Code_Quality/What_is_a_style_guide.md"}'),s={name:"senior_growth/Clean_code_and_Code_Quality/What_is_a_style_guide.md"};function r(i,e,l,d,c,g){return n(),o("div",null,[...e[0]||(e[0]=[a('<h1 id="what-is-a-style-guide" tabindex="-1">What is a style guide? <a class="header-anchor" href="#what-is-a-style-guide" aria-label="Permalink to ‚ÄúWhat is a style guide?‚Äù">‚Äã</a></h1><h1 id="üé®-style-guide-in-software-development" tabindex="-1">üé® Style Guide in Software Development <a class="header-anchor" href="#üé®-style-guide-in-software-development" aria-label="Permalink to ‚Äúüé® Style Guide in Software Development‚Äù">‚Äã</a></h1><p>A <strong>style guide</strong> is a <strong>document or set of rules</strong> that defines how code should be <strong>written, formatted, and structured</strong> in a project or organization. It ensures <strong>consistency and readability</strong> across the codebase, making it easier for teams to maintain and scale software.</p><hr><h2 id="_1Ô∏è‚É£-purpose-of-a-style-guide" tabindex="-1">1Ô∏è‚É£ Purpose of a Style Guide <a class="header-anchor" href="#_1Ô∏è‚É£-purpose-of-a-style-guide" aria-label="Permalink to ‚Äú1Ô∏è‚É£ Purpose of a Style Guide‚Äù">‚Äã</a></h2><ul><li><strong>Consistency:</strong> All developers follow the same conventions, reducing cognitive load.</li><li><strong>Readability:</strong> Easier for team members to understand code quickly.</li><li><strong>Maintainability:</strong> Simplifies code reviews, debugging, and refactoring.</li><li><strong>Collaboration:</strong> Reduces conflicts and misunderstandings in team environments.</li><li><strong>Tool Integration:</strong> Works with linters, formatters, and CI/CD pipelines for automated enforcement.</li></ul><hr><h2 id="_2Ô∏è‚É£-common-components-of-a-style-guide" tabindex="-1">2Ô∏è‚É£ Common Components of a Style Guide <a class="header-anchor" href="#_2Ô∏è‚É£-common-components-of-a-style-guide" aria-label="Permalink to ‚Äú2Ô∏è‚É£ Common Components of a Style Guide‚Äù">‚Äã</a></h2><table tabindex="0"><thead><tr><th>Component</th><th>Examples</th></tr></thead><tbody><tr><td><strong>Formatting</strong></td><td>Indentation (2 vs 4 spaces), line length, spaces vs tabs, semicolons</td></tr><tr><td><strong>Naming conventions</strong></td><td>camelCase for variables, PascalCase for components, UPPER_CASE for constants</td></tr><tr><td><strong>File/folder structure</strong></td><td>Grouping by feature/module, naming conventions for files</td></tr><tr><td><strong>Code patterns</strong></td><td>Preferred ways to write loops, functions, or hooks</td></tr><tr><td><strong>Comments/documentation</strong></td><td>When and how to comment code; docstring standards</td></tr><tr><td><strong>Best practices</strong></td><td>Immutability, error handling, side-effect management, accessibility standards in UI</td></tr><tr><td><strong>Testing</strong></td><td>Where and how tests should be written (unit, integration, E2E)</td></tr></tbody></table><hr><h2 id="_3Ô∏è‚É£-examples-of-style-guides" tabindex="-1">3Ô∏è‚É£ Examples of Style Guides <a class="header-anchor" href="#_3Ô∏è‚É£-examples-of-style-guides" aria-label="Permalink to ‚Äú3Ô∏è‚É£ Examples of Style Guides‚Äù">‚Äã</a></h2><ul><li><strong>JavaScript / TypeScript:</strong><ul><li>Airbnb JavaScript Style Guide</li><li>Google JavaScript Style Guide</li><li>StandardJS</li></ul></li><li><strong>CSS / SCSS / Styled Components:</strong><ul><li>CSS Guidelines by Harry Roberts</li><li>Stylelint recommended rules</li></ul></li><li><strong>React / Frontend:</strong><ul><li>React/JSX rules for hooks, component naming, props handling</li></ul></li></ul><hr><h2 id="_4Ô∏è‚É£-enforcement" tabindex="-1">4Ô∏è‚É£ Enforcement <a class="header-anchor" href="#_4Ô∏è‚É£-enforcement" aria-label="Permalink to ‚Äú4Ô∏è‚É£ Enforcement‚Äù">‚Äã</a></h2><ul><li><strong>Linters:</strong> ESLint, TSLint, Stylelint enforce style guide rules automatically.</li><li><strong>Formatters:</strong> Prettier ensures consistent formatting.</li><li><strong>Pre-commit hooks:</strong> Husky + lint-staged prevents code that violates the style guide from being committed.</li><li><strong>CI/CD pipelines:</strong> Ensure all PRs comply with style guide rules before merging.</li></ul><hr><h2 id="‚úÖ-key-takeaways" tabindex="-1">‚úÖ Key Takeaways <a class="header-anchor" href="#‚úÖ-key-takeaways" aria-label="Permalink to ‚Äú‚úÖ Key Takeaways‚Äù">‚Äã</a></h2><ul><li><strong>Style guide = team-agreed conventions for writing code</strong></li><li>Ensures <strong>consistent, readable, maintainable code</strong> across projects</li><li>Works hand-in-hand with <strong>linters, formatters, and automated pipelines</strong></li><li>Makes <strong>code reviews faster and more effective</strong> because reviewers focus on logic, not style</li></ul><blockquote><p>üí° Senior Tip:<br> A style guide is not just about aesthetics‚Äîit‚Äôs <strong>a contract that communicates code intent and quality</strong> across the team.</p></blockquote>',19)])])}const h=t(s,[["render",r]]);export{m as __pageData,h as default};
